mapper.select:
select  task.id, task.gmt_create, task.gmt_modified,task.process_instance_id, process_definition_id_and_version,
activity_instance_id,process_definition_activity_id, execution_instance_id,claim_user_id,priority,claim_time,complete_time,status
from  se_task_instance task,se_task_assignee_instance assignee where task.id = assignee.task_instance_id
  and  task.status = #{status} 
  and  task.activity_instance_id = #{activityInstanceId} 
  and  task.process_definition_activity_id = #{processDefinitionActivityId} 
 and task.process_instance_id = #{processInstanceId}
 and assignee.assignee_id =  #{assigneeUserId} and assignee.assignee_type = 'user' 
order by task.gmt_modified desc
limit #{pageOffSide},#{pageSize}

select  count(task.id)
from  se_task_instance task,se_task_assignee_instance assignee where task.id = assignee.task_instance_id
  and  task.status = #{status} 
  and  task.activity_instance_id = #{activityInstanceId} 
  and  task.process_definition_activity_id = #{processDefinitionActivityId} 
 and task.process_instance_id = #{processInstanceId}
 and assignee.assignee_id =  #{assigneeId} and assignee.assignee_type = 'user' 
order by task.gmt_modified desc
limit #{pageOffSide},#{pageSize}
